<?phpclass WC_Scalapay_Gateway_Admin {    private $plugin_name;    private $version;    public function __construct($plugin_name, $version) {        $this->plugin_name = $plugin_name;        $this->version = $version;    }    public function backend_enqueue_styles() {        wp_enqueue_style($this->plugin_name, WC_SCALAPAY_GATEWAY_ASSETS . 'css/scalapay_admin.css');    }    public function backend_enqueue_scripts() {        wp_enqueue_script($this->plugin_name, WC_SCALAPAY_GATEWAY_ASSETS . 'js/scalapay_admin.js');    }    public function frontend_enqueue_scripts() {        wp_enqueue_style($this->plugin_name, WC_SCALAPAY_GATEWAY_ASSETS . 'css/scalapay.css', array(), $this->version, 'all');        wp_register_script('payin3-scalapay-widget.esm','https://cdn.scalapay.com/widget/v3/js/scalapay-widget.esm.js');        wp_register_script('payin3-scalapay-widget','https://cdn.scalapay.com/widget/v3/js/scalapay-widget.js');        wp_enqueue_script('payin3-scalapay-widget.esm');        wp_enqueue_script('payin3-scalapay-widget');    }    public function add_type_attribute_scalapay($tag, $handle,$src) {        if ( 'payin3-scalapay-widget.esm' === $handle){            $tag = '<script type="module" src="'.esc_url($src).'"></script>';        }        if ( 'payin3-scalapay-widget' === $handle){            $tag = '<script nomodule src="'.esc_url($src).'"></script>';        }        return $tag;    }    public function init_wc_scalapay_payment() {        if (class_exists('WC_Payment_Gateway')) {            if (!class_exists('WC_Scalapay_Gateway_Payin3')) {                include_once( WC_SCALAPAY_GATEWAY_DIR_PATH . 'classes/gateways/payin3/class-scalapay-payin3.php' );            }            if (!class_exists('WC_Scalapay_Gateway_Payin4')) {                include_once( WC_SCALAPAY_GATEWAY_DIR_PATH . 'classes/gateways/payin4/class-scalapay-payin4.php' );            }            if (!class_exists('WC_Scalapay_Gateway_Later')) {                include_once( WC_SCALAPAY_GATEWAY_DIR_PATH . 'classes/gateways/paylater/class-scalapay-paylater.php' );            }        }    }    public function wc_scalapay_add_payment_method_class($methods) {        if (class_exists('WC_Payment_Gateway')) {            $methods[] = 'WC_Scalapay_Gateway_Payin3';            $methods[] = 'WC_Scalapay_Gateway_Payin4';            $methods[] = 'WC_Scalapay_Gateway_Later';            return $methods;        }    }    public function display_scalapay_widget_product_payin3() {        // display payin3 widget        global $product;        echo '<span class="scalapay-product-price-selectors">'.$product->get_price().'</span>';        $gatewayObj = new WC_Scalapay_Gateway_Payin3();        if ($gatewayObj->enabled == esc_attr('yes')) {            $numberOfInstallments = esc_attr(3);            require WC_SCALAPAY_GATEWAY_DIR_PATH.'includes/scalapay-widget.php';        }    }    public function display_scalapay_widget_product_payin4() {        // display payin4 widget        $gatewayObj = new WC_Scalapay_Gateway_Payin4();        if ($gatewayObj->enabled == esc_attr('yes')) {            $numberOfInstallments = esc_attr(4);            require WC_SCALAPAY_GATEWAY_DIR_PATH.'includes/scalapay-widget.php';        }    }    public function display_scalapay_widget_product_paylater() {        // display paylater widget        $gatewayObj = new WC_Scalapay_Gateway_Later();        if ($gatewayObj->enabled == esc_attr('yes')) {            $numberOfInstallments = esc_attr(1);            require WC_SCALAPAY_GATEWAY_DIR_PATH.'includes/scalapay-widget.php';        }    }    public function display_scalapay_widget_cart_payin3() {        if (is_cart()) {            $cart_total = WC()->cart->get_total( 'raw' );            echo '<span class="scalapay-cart-price-selectors">'.$cart_total.'</span>';        }        // display widget        $gatewayObj = new WC_Scalapay_Gateway_Payin3();        if ($gatewayObj->enabled == esc_attr('yes')) {            $numberOfInstallments = esc_attr(3);            require WC_SCALAPAY_GATEWAY_DIR_PATH.'includes/scalapay-widget.php';        }    }    public function display_scalapay_widget_cart_payin4() {        // display payin4 widget        $gatewayObj = new WC_Scalapay_Gateway_Payin4();        if ($gatewayObj->enabled == esc_attr('yes')) {            $numberOfInstallments = esc_attr(4);            require WC_SCALAPAY_GATEWAY_DIR_PATH.'includes/scalapay-widget.php';        }    }    public function display_scalapay_widget_cart_paylater() {        // display paylater widget        $gatewayObj = new WC_Scalapay_Gateway_Later();        if ($gatewayObj->enabled == esc_attr('yes')) {            $numberOfInstallments = esc_attr(1);            require WC_SCALAPAY_GATEWAY_DIR_PATH.'includes/scalapay-widget.php';        }    }    public function scalapay_price_selectors() {    }}